class TestWriteSidbLayoutToSvgInSiqadLayout(unittest.TestCase):

    def test_write_sidb_cell_level_layout_to_svg(self):

        # Create and configure the SIDB layout
        sidb_layout = sidb_100_lattice((4, 4))
        sidb_layout.assign_cell_type((0, 0), sidb_technology.cell_type.NORMAL)
        sidb_layout.assign_cell_type((1, 1), sidb_technology.cell_type.NORMAL)
        sidb_layout.assign_cell_type((1, 0), sidb_technology.cell_type.NORMAL)
        sidb_layout.assign_cell_type((3, 3), sidb_technology.cell_type.NORMAL)

        params = write_sidb_layout_svg_in_siqad_style_params()

        params.cmode = color_mode.DARK
        generated_svg_cell_level_dark_mode = write_sidb_layout_to_svg_and_return_as_string(sidb_layout, params)
        self.assertEqual(generated_svg_cell_level_dark_mode, cell_level_dark_mode)

        params.cmode = color_mode.LIGHT
        generated_svg_cell_level_light_mode = write_sidb_layout_to_svg_and_return_as_string(sidb_layout, params)
        print(generated_svg_cell_level_light_mode)
        self.assertEqual(generated_svg_cell_level_light_mode, cell_level_light_mode)

    def test_write_sidb_charge_distribution_to_svg(self):

        # Create and configure the SIDB layout
        sidb_layout = sidb_100_lattice((4, 4))
        sidb_layout.assign_cell_type((0, 0), sidb_technology.cell_type.NORMAL)
        sidb_layout.assign_cell_type((1, 1), sidb_technology.cell_type.NORMAL)
        sidb_layout.assign_cell_type((1, 0), sidb_technology.cell_type.NORMAL)
        sidb_layout.assign_cell_type((3, 3), sidb_technology.cell_type.NORMAL)

        cds = charge_distribution_surface_100(sidb_layout)
        cds.assign_charge_state((0, 0), sidb_charge_state.NEGATIVE)
        cds.assign_charge_state((1, 1), sidb_charge_state.NEGATIVE)
        cds.assign_charge_state((1, 0), sidb_charge_state.NEUTRAL)
        cds.assign_charge_state((3, 3), sidb_charge_state.POSITIVE)

        params = write_sidb_layout_svg_in_siqad_style_params()

        params.cmode = color_mode.DARK
        generated_svg_cds_dark_mode = write_sidb_layout_to_svg_and_return_as_string(cds, params)
        self.assertEqual(generated_svg_cds_dark_mode, cds_dark_mode)

        params.cmode = color_mode.LIGHT
        generated_svg_cds_light_mode = write_sidb_layout_to_svg_and_return_as_string(cds, params)
        self.assertEqual(generated_svg_cds_light_mode, cds_light_mode)

if __name__ == '__main__':
    unittest.main()
